import { PluginDefinition, RuntimeContext } from 'skeleton-crew-runtime';

interface TodoItem {
  id: number;
  text: string;
  completed: boolean;
}

export const todoPlugin: PluginDefinition = {
  name: 'todo',
  version: '1.0.0',
  setup(context: RuntimeContext) {
    const todos: TodoItem[] = [];
    let nextId = 1;

    // Register screen
    context.screens.registerScreen({
      id: 'todo-list',
      title: 'Todo List',
      component: 'TodoListScreen',
      metadata: { todos }
    });

    // Add todo action
    context.actions.registerAction({
      id: 'todo:add',
      handler: ({ text }) => {
        const todo: TodoItem = {
          id: nextId++,
          text,
          completed: false
        };
        todos.push(todo);
        context.events.emit('todo:added', { todo });
        return { todo };
      }
    });

    // Toggle todo action
    context.actions.registerAction({
      id: 'todo:toggle',
      handler: ({ id }) => {
        const todo = todos.find(t => t.id === id);
        if (!todo) {
          throw new Error(`Todo ${id} not found`);
        }
        todo.completed = !todo.completed;
        context.events.emit('todo:toggled', { todo });
        return { todo };
      }
    });

    // Delete todo action
    context.actions.registerAction({
      id: 'todo:delete',
      handler: ({ id }) => {
        const index = todos.findIndex(t => t.id === id);
        if (index === -1) {
          throw new Error(`Todo ${id} not found`);
        }
        const [deleted] = todos.splice(index, 1);
        context.events.emit('todo:deleted', { todo: deleted });
        return { todo: deleted };
      }
    });

    // Get all todos action
    context.actions.registerAction({
      id: 'todo:get-all',
      handler: () => {
        return { todos: [...todos] };
      }
    });
  }
};

